mapper.select:
SELECT * 
FROM AUTH_ORGANIZATION

		AND ID = #{id,jdbcType=VARCHAR}

SELECT AuthOrganization.ID AS id , AuthOrganization.NAME AS name , AuthOrganization.PARENT_ID AS parentId , AuthOrganization.ROOT_ID AS rootId , AuthOrganization.CODE AS code , AuthOrganization.IS_USE_ROOT_MENU AS isUseRootMenu , AuthOrganization.TYPE1 AS type1 , AuthOrganization.TYPE2 AS type2 , AuthOrganization.DESCRIPTION AS description , AuthOrganization.UPDATE_TIME AS updateTime , AuthOrganization.CREATE_TIME AS createTime , parentIdAuthOrganization.NAME AS parentIdName , rootIdAuthOrganization.NAME AS rootIdName
FROM AUTH_ORGANIZATION AuthOrganization LEFT JOIN AUTH_ORGANIZATION parentIdAuthOrganization ON AuthOrganization.PARENT_ID = parentIdAuthOrganization.ID LEFT JOIN AUTH_ORGANIZATION rootIdAuthOrganization ON AuthOrganization.ROOT_ID = rootIdAuthOrganization.ID
AND authOrganization.ID = #{id,jdbcType=VARCHAR}

SELECT AuthOrganization.ID AS id , AuthOrganization.NAME AS name , AuthOrganization.PARENT_ID AS parentId , AuthOrganization.ROOT_ID AS rootId , AuthOrganization.CODE AS code , AuthOrganization.IS_USE_ROOT_MENU AS isUseRootMenu , AuthOrganization.TYPE1 AS type1 , AuthOrganization.TYPE2 AS type2 , AuthOrganization.DESCRIPTION AS description , AuthOrganization.UPDATE_TIME AS updateTime , AuthOrganization.CREATE_TIME AS createTime , parentIdAuthOrganization.NAME AS parentIdName , rootIdAuthOrganization.NAME AS rootIdName
FROM AUTH_ORGANIZATION AuthOrganization LEFT JOIN AUTH_ORGANIZATION parentIdAuthOrganization ON AuthOrganization.PARENT_ID = parentIdAuthOrganization.ID LEFT JOIN AUTH_ORGANIZATION rootIdAuthOrganization ON AuthOrganization.ROOT_ID = rootIdAuthOrganization.ID
AND AuthOrganization.ID = #{vo.id,jdbcType=VARCHAR} AND AuthOrganization.NAME LIKE CONCAT('%',#{vo.name,jdbcType=VARCHAR},'%') AND AuthOrganization.PARENT_ID = #{vo.parentId,jdbcType=VARCHAR} AND AuthOrganization.ROOT_ID = #{vo.rootId,jdbcType=VARCHAR} AND AuthOrganization.CODE LIKE CONCAT('%',#{vo.code,jdbcType=VARCHAR},'%') AND AuthOrganization.IS_USE_ROOT_MENU = #{vo.isUseRootMenu,jdbcType=VARCHAR} AND AuthOrganization.TYPE1 = #{vo.type1,jdbcType=VARCHAR} AND AuthOrganization.TYPE2 = #{vo.type2,jdbcType=VARCHAR} AND AuthOrganization.DESCRIPTION = #{vo.description,jdbcType=VARCHAR} AND AuthOrganization.UPDATE_TIME = #{vo.updateTime,jdbcType=VARCHAR} AND AuthOrganization.CREATE_TIME = #{vo.createTime,jdbcType=VARCHAR}

